/* inseÃ±amos el tiempo */
var showColon = 0;
export function getCurrentDateTime() {
    let resTime   = '';
    let currentDate = new Date();
    let hours = currentDate.getHours().toString().padStart(2, '0');
    let minutes = currentDate.getMinutes().toString().padStart(2, '0');

    let day = currentDate.getDate().toString().padStart(2, '0');

    let mes = currentDate.getMonth().toString().padStart(2, '0');
    let stringTimeColon = hours+':'+minutes; 

    let stringWithOutColon = hours+' '+minutes;
    if(showColon == 0) { resTime = stringTimeColon; showColon = 1;
    } else { resTime = stringWithOutColon; showColon = 0; }
    // document.getElementById('spanDate').innerText = ' '+day+'/'+mes;
    // document.getElementById('spanTime').innerHTML = resTime;
    return {date:' '+day+'/'+mes, time: hours+':'+minutes};
}
