import React, { useEffect } from 'react';
import { listArticles } from "../vars/global";

const miClickEventA = (event) => {
    console.log('Key pressed:', event.key);
};


const Ajustes = () => {
    useEffect(() => {
        console.log(listArticles)
        document.title = "Ajustes - Indicadores produccíon";
    }, [])

    return (
        <div>
            <header>
                
                <img src="/logo512.png" class="header_image" id="header_image" />
                <span class="page_title">Ajustes</span>
               
            </header>
            <section>
                <div><button type="button" class="btn-close" id="closeButton"></button></div>
                <table class="table table-striped">
                    <thead><tr>
                        <th scope="col">Numero</th>
                        <th scope="col">Descripcion</th>
                        <th scope="col">Ritmo mínimo</th>
                        <th scope="col">Ritmo máximo</th>
                        <th scope="col">Peso nominal</th>
                        <th scope="col">Peso máximo</th>
                    </tr></thead>
                    <tbody>
                    {listArticles.map(article => (
                            <tr key={article.id}>
                                <td><input id={`numero_${article.id}`} type="text" value={article.num} /></td>
                                <td><input id={`producto_${article.id}`} type="text" className="description" value={article.desc} /></td>
                                <td><input id={`min_${article.id}`} type="number" value={article.ritmoMin} /></td>
                                <td><input id={`max_${article.id}`} type="number" value={article.ritmoMax} /></td>
                                <td><input id={`pesonominal_${article.id}`} type="number" value={article.pesoNom} /></td>
                                <td><input onKeyDown={miClickEventA} id={`peso_${article.id}`} type="number" value={article.pesoMax} /></td>
                            </tr>
                        ))}

                    </tbody>
                </table>
                <div><button type="button" class="btn btn-primary" id="saveButton">Guardar y salir</button></div>
            </section>
        </div >
    );
};

export default Ajustes;
